import styled from "styled-components";
import Text from "./ui-components/text";
import {Table} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import {useState} from "react";

const Wrapper = styled.div`
  min-height: 100vh;
  padding: 0 15px;
  display: flex;
  flex-direction: column;
`;

function App() {
    const [state, setState] = useState([{
        id: 14,
        email: "antonida@mail.com",
        first_name: "Antonida",
        pay_status: false,
        last_name: "White",
        username: "AntonidaW",
        profile_link: "https://fizikl.ru/"
    }, {
        id: 17,
        email: "butonchick@gmail.com",
        first_name: "",
        pay_status: true,
        last_name: "Zorro",
        username: "ananimus007",
        profile_link: "https://translate.google.com/?sl=de&tl=ru&text=Herzlich%20willkommen!&op=translate"
    }, {
        id: 8,
        email: "vasya2007@mail.ru",
        first_name: "Вася",
        pay_status: false,
        last_name: "Пупкин",
        username: "superVasssya",
        profile_link: ""
    }, {
        id: 12,
        email: "antonio@mail.com",
        first_name: "Anton",
        pay_status: true,
        last_name: "Black",
        username: "ABlack",
        profile_link: "https://translate.google.com/"
    }])

    const SortByName = () => {
        let arr = state
        arr.sort((a, b) => a.first_name.localeCompare(b.first_name))
        setState(arr)
        console.log("Arr", arr)
        console.log("State", state)
    }


    return (
        <Wrapper>
            <div style={{margin: "200px auto"}}>
                <Text color={"black"} fontSize={"44px"} fontWeight={600}>
                    Users
                </Text>
                <div style={{marginTop: "20px", textAlign: "center"}}>
                    <Table striped bordered hover>
                        <thead>
                        <tr>
                            <th>Id</th>
                            <th style={{cursor: "pointer"}} onClick={SortByName}>First Name</th>
                            <th>Last Name</th>
                            <th>Username</th>
                            <th>Email</th>
                            <th>Pay status</th>
                            <th>Link</th>
                        </tr>
                        </thead>
                        <tbody>
                        {state.map((item, index) => (
                            <tr key={index}>
                                <td>{item.id ? item.id : "-"}</td>
                                <td>{item.first_name ? item.first_name : "-"}</td>
                                <td>{item.last_name ? item.last_name : "-"}</td>
                                <td>{item.username ? item.username : "-"}</td>
                                <td>{item.email ? item.email : "-"}</td>
                                <td>{item.pay_status ? "Оплатил" : "Не оплатил"}</td>
                                <td>
                                    <a href={item.profile_link}>
                                        {item.profile_link && "Тык"}
                                    </a>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </Table>
                </div>
            </div>
        </Wrapper>
    );
}

export default App;
