import {Button, Col, Form, Modal, Row} from "react-bootstrap";
import {useState} from "react";

let MyModal = ({showModal, setShowModal, state, setState}) => {
    console.log(state)
    console.log(state[showModal.index])

    const [validated, setValidated] = useState(false);
    const [localState, setLocalState] = useState({
        id: state[showModal.index].id,
        email: state[showModal.index].email,
        first_name: state[showModal.index].first_name,
        didPay: state[showModal.index].didPay,
        last_name: state[showModal.index].last_name,
        username: state[showModal.index].username,
        profile_link: state[showModal.index].profile_link
    })

    const handleSubmit = (event) => {
        event.preventDefault();
        setShowModal({...showModal, visible: false})
        setState(state.map((item, index) => {
            if (index === showModal.index)
                return {
                    id: localState.id,
                    email: localState.email,
                    first_name: localState.first_name,
                    didPay: localState.didPay,
                    last_name: localState.last_name,
                    username: localState.username,
                    profile_link: localState.profile_link
                }
            else return item
        }))
    };

    return (
        <Modal show={showModal.visible} onHide={() => setShowModal({...showModal, visible: false})}>
            <Modal.Header closeButton>
                <Modal.Title>Change data</Modal.Title>
            </Modal.Header>
            <Form noValidate onSubmit={handleSubmit}>
                <Row style={{margin: "0 20px"}} className="mb-3">
                    <Form.Group as={Col} md="12" controlId="validationCustom01">
                        <Form.Control
                            style={{marginBottom: "10px"}}
                            required
                            type="text"
                            placeholder="Id"
                            value={localState.id}
                            onChange={(event) => setLocalState({...state, id: event.target.value})}
                        />
                        <Form.Control
                            style={{marginBottom: "10px"}}
                            required
                            type="text"
                            placeholder="First name"
                            value={localState.first_name}
                            onChange={(event) => setLocalState({...state, first_name: event.target.value})}
                        />
                        <Form.Control
                            style={{marginBottom: "10px"}}
                            required
                            type="text"
                            placeholder="Last name"
                            value={localState.last_name}
                            onChange={(event) => setLocalState({...state, last_name: event.target.value})}
                        />
                        <Form.Control
                            style={{marginBottom: "10px"}}
                            required
                            type="text"
                            placeholder="Username"
                            value={localState.username}
                            onChange={(event) => setLocalState({...state, username: event.target.value})}
                        />
                        <Form.Control
                            style={{marginBottom: "10px"}}
                            required
                            type="text"
                            placeholder="Email"
                            value={localState.email}
                            onChange={(event) => setLocalState({...state, email: event.target.value})}
                        />
                        <Form.Control
                            style={{marginBottom: "10px"}}
                            required
                            type="text"
                            placeholder="Pay status"
                            value={localState.didPay}
                            onChange={(event) => setLocalState({...state, didPay: event.target.value})}
                        />
                        <Form.Control
                            style={{marginBottom: "10px"}}
                            required
                            type="text"
                            placeholder="Link"
                            value={localState.profile_link}
                            onChange={(event) => setLocalState({...state, profile_link: event.target.value})}
                        />
                    </Form.Group>
                </Row>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    justifyContent: 'end',
                    paddingRight: "20px",
                    marginBottom: "20px"
                }}>
                    <Button style={{marginRight: "20px"}} type="submit">Save Changes</Button>
                    <Button variant="secondary" onClick={() => setShowModal({...showModal, visible: false})}>
                        Close
                    </Button>
                </div>
            </Form>
        </Modal>
    )
}

export default MyModal;
