import styled from "styled-components";
import Text from "./ui-components/text";
import {Table} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import {useEffect, useState} from "react";
import {SortById} from "./helpers/sort";

const Wrapper = styled.div`
  min-height: 100vh;
  padding: 0 15px;
  display: flex;
  flex-direction: column;
`;

function App() {
    const [state, setState] = useState([{
        id: 14,
        email: "antonida@mail.com",
        first_name: "Antonida",
        pay_status: false,
        last_name: "White",
        username: "AntonidaW",
        profile_link: "https://fizikl.ru/"
    }, {
        id: 17,
        email: "butonchick@gmail.com",
        first_name: "",
        pay_status: true,
        last_name: "Zorro",
        username: "ananimus007",
        profile_link: "https://translate.google.com/?sl=de&tl=ru&text=Herzlich%20willkommen!&op=translate"
    }, {
        id: 8,
        email: "vasya2007@mail.ru",
        first_name: "Вася",
        pay_status: false,
        last_name: "Пупкин",
        username: "superVasssya",
        profile_link: ""
    }, {
        id: 12,
        email: "antonio@mail.com",
        first_name: "Anton",
        pay_status: true,
        last_name: "Black",
        username: "ABlack",
        profile_link: "https://translate.google.com/"
    }, {
        id: 12,
        email: "antonio@mail.com",
        first_name: "Baton",
        pay_status: true,
        last_name: "Black",
        username: "ABlack",
        profile_link: "https://translate.google.com/"
    },{
        id: 12,
        email: "antonio@mail.com",
        first_name: "Clint",
        pay_status: true,
        last_name: "Black",
        username: "ABlack",
        profile_link: "https://translate.google.com/"
    },])

/*    const SortByName = () => {
        if (count.sortByName % 2 === 0 )
            setState(state.sort(( a, b ) => a.first_name.localeCompare(b.first_name)))
        else setState(state.sort(( a, b ) => b.first_name.localeCompare(a.first_name)))
        setCount({...count, sortByName: count.sortByName + 1})
    }

    const SortByLastName = () => {
        if (count.sortByLastName % 2 === 0 )
            setState(state.sort(( a, b ) => a.last_name.localeCompare(b.last_name)))
        else setState(state.sort(( a, b ) => b.last_name.localeCompare(a.last_name)))
        setCount({...count, sortByLastName: count.sortByLastName + 1})
    }

    const SortByEmail = () => {
        if (count.sortByEmail % 2 === 0 )
            setState(state.sort(( a, b ) => a.email.localeCompare(b.email)))
        else setState(state.sort(( a, b ) => b.email.localeCompare(a.email)))
        setCount({...count, sortByEmail: count.sortByEmail + 1})
    }

    const SortByUsername = () => {
        if (count.sortByUsername % 2 === 0 )
            setState(state.sort(( a, b ) => a.username.localeCompare(b.username)))
        else setState(state.sort(( a, b ) => b.username.localeCompare(a.username)))
        setCount({...count, sortByUsername: count.sortByUsername + 1})
    }

    const SortByPayStatus = () => {
        if (count.sortByPayStatus % 2 === 0 )
            setState(state.sort(( a, b ) => a.pay_status - b.pay_status))
        else setState(state.sort(( a, b ) => b.pay_status - a.pay_status))
        setCount({...count, sortByPayStatus: count.sortByPayStatus + 1})
    }

    const SortById = () => {
        if (count.sortById % 2 === 0 )
            setState(state.sort(( a, b ) => a.id - b.id))
        else setState(state.sort(( a, b ) => b.id - a.id))
        setCount({...count, sortById: count.sortById + 1})
    }

    const [count, setCount] = useState({
        sortById: 0,
        sortByName: 0,
        sortByLastName: 0,
        sortByUsername: 0,
        sortByEmail: 0,
        sortByPayStatus: 0,
        sortByLink: 0
    });*/

    return (
        <Wrapper>
            <div style={{margin: "200px auto"}}>
                <Text color={"black"} fontSize={"44px"} fontWeight={600}>
                    Users
                </Text>
                <div style={{marginTop: "20px", textAlign: "center"}}>
                    <Table striped bordered hover>
                        <thead>
                        <tr>
                            <th style={{cursor: "pointer"}} onClick={() => SortById(state, setState)}>Id</th>
                           {/* <th style={{cursor: "pointer"}} onClick={() => SortByName()}>First Name</th>
                            <th style={{cursor: "pointer"}} onClick={() => SortByLastName()}>Last Name</th>
                            <th style={{cursor: "pointer"}} onClick={() => SortByUsername()}>Username</th>
                            <th style={{cursor: "pointer"}} onClick={() => SortByEmail()}>Email</th>
                            <th style={{cursor: "pointer"}} onClick={() => SortByPayStatus()}>Pay status</th>*/}
                            <th>Link</th>
                        </tr>
                        </thead>
                        <tbody>
                        {state.map((item, index) => (
                            <tr key={index}>
                                <td>{item.id ? item.id : "-"}</td>
                                <td>{item.first_name ? item.first_name : "-"}</td>
                                <td>{item.last_name ? item.last_name : "-"}</td>
                                <td>{item.username ? item.username : "-"}</td>
                                <td>{item.email ? item.email : "-"}</td>
                                <td>{item.pay_status ? "Оплатил" : "Не оплатил"}</td>
                                <td>
                                    <a href={item.profile_link}>
                                        {item.profile_link && "Тык"}
                                    </a>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </Table>
                </div>
            </div>
        </Wrapper>
    );
}

export default App;
