import {Button, Col, Form, Modal, Row} from "react-bootstrap";
import {useState} from "react";

let MyModal = ({showModal, setShowModal, state, setState}) => {
    console.log(state[showModal.index])

    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        event.preventDefault();
        if (form.checkValidity() === false) {
            event.stopPropagation();
        }
        setValidated(true);
    };

    return (
        <Modal show={showModal.visible} onHide={() => setShowModal({...showModal, visible: false})}>
            <Modal.Header closeButton>
                <Modal.Title>Change data</Modal.Title>
            </Modal.Header>
            <Form noValidate validated={validated} onSubmit={handleSubmit}>
                <Form.Group as={Col} md="12" controlId="validationCustom01">
                    <Form.Control
                        required
                        type="text"
                        placeholder="Search..."
                    />
                    <Form.Control.Feedback type="invalid">
                        Введите какое-нибудь значение
                    </Form.Control.Feedback>
                </Form.Group>
                <Button type="submit">Search</Button>
            </Form>
            <Modal.Footer>
                <Button variant="secondary" onClick={() => setShowModal({...showModal, visible: false})}>
                    Close
                </Button>
                <Button variant="primary" onClick={() => setShowModal({...showModal, visible: false})}>
                    Save Changes
                </Button>
            </Modal.Footer>
        </Modal>
    )
}

export default MyModal;
